    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
      if(this.validardatos())
      {
        Medico mediconuevo = new Medico(this.txtMatricula.getText(),this.txtNombre.getText(),this.txtApellido.getText(),new Especialidad(this.txtEspecialidad.getText()));
       mediconuevo.almacenarMedico();
        this.hide();
      }
      else
      {
          JOptionPane.showMessageDialog(this, "Debe ingresar todos los datos.");
      }
       
    }//GEN-LAST:event_jButton1ActionPerformed

